mapper.update:
UPDATE Account
SET account_user_id = #{account_user_id},
    account_item_id = #{account_item_id},
    account_pw = #{account_pw},
    account_balance = #{account_balance}
WHERE account_num = #{account_num}

mapper.select:
SELECT COUNT(*)
FROM Account a JOIN User u ON a.account_user_id = u.id



			WHERE a.account_num LIKE #{keyword}


			WHERE u.user_id LIKE #{keyword}

SELECT COUNT(account_num)
FROM Account
WHERE account_num = #{account_num}

SELECT COUNT(*)
FROM Account a JOIN User u ON a.account_user_id = u.user_id
WHERE u.user_id = #{user_id}



			AND a.account_num LIKE #{keyword}


			AND u.user_id LIKE #{keyword}

SELECT a.account_num, a.account_user_id, a.account_item_id, a.account_pw, a.account_balance, a.account_date, u.user_id account_user_name FROM Account a JOIN User u ON a.account_user_id = u.user_id WHERE u.user_id = #{user_id}
AND a.account_num LIKE #{keyword} ORDER BY a.account_num
ORDER BY u.user_id
LIMIT #{from}, #{row_per_page}
